package d.Module.Math;

public class A_Math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.max(5, 10)); //Finds the greater value
		System.out.println(Math.min(5, 10)); //Finds the lesser value
		System.out.println(Math.sqrt(25)); //Finds the square root
		System.out.println(Math.abs(-8.7)); //Find the absolute value
		System.out.println(Math.round(Math.random() * 101)); //Picks a random number 0-100 and rounds to the nearest 1.
	}

}
